import React, { useState, useEffect } from 'react';
import * as math from 'mathjs';
import '../App.css';
import Topbar from './Topbar';
import { Container } from 'react-bootstrap';
import {XYPlot, XAxis, YAxis, HorizontalGridLines, VerticalGridLines, LineSeries} from 'react-vis';
import { render } from '@testing-library/react';

export default class Bisection extends Component {
  state = {
    equation: "",
    btnState: 0,
    xl: 0,
    xr: 0
  }

  setEquation = (e) =>{
    this.setState({
      equation: e.target.value
    });
  }

  setBtnState = (e) =>{
    this.setState({
      btnState: e.target.value
    });
  }

  setXL = (e) =>{
    this.setState({
      xl: e.target.value
    });
  }

  setXR = (e) =>{
    this.setState({
      xr: e.target.value
    });
  }

  handleSubmit = (e) => {
    if(btnState === 0)
    {
      e.preventDefault();
      bisection();
      setBtnState(1);
    }
  };

  bisection = () => {
    let table = document.getElementById("output");
    var xm = 0;
    var n = 0;
    var check = parseFloat(0.0000000);
    do{
      xm = findxm(xl,xr);
      console.log(check);//error
      n++;
      console.log(n);
      if(funcal(xl)*funcal(xm)>0) {
        check=Math.abs((xm-xl)/xm).toFixed(8);
        xl=xm;
      }
      else {
        check=Math.abs((xm-xr)/xm).toFixed(8);
        xr=xm;
      }
      // Create an empty <tr> element and add it to the 1st position of the table:
      let row = table.insertRow(n);

      // Insert new cells (<td> elements) at the 1st and 2nd position of the "new" <tr> element:
      var cell1 = row.insertCell(0);
      var cell2 = row.insertCell(1);
      var cell3 = row.insertCell(2);
      var cell4 = row.insertCell(3);
      var cell5 = row.insertCell(4);

      // Add some text to the new cells:
      cell1.innerHTML = n;
      cell2.innerHTML = xl;
      cell3.innerHTML = xr;
      cell4.innerHTML = xm;
      cell5.innerHTML = check;
    }while(check>0.000001)
  }
  //หาXm ปกติ
  findxm = (xl,xr) => {
    return (parseFloat(xl)+parseFloat(xr))/2
  }

  // แก้สมาการ X ใช้ไลบรารี่แมทจ้าา
  funcal = (X) => {
    const expression = equation;
    const expr = math.compile(expression);
    let scope = {x:parseFloat(X)};
    return expr.evaluate(scope);
  }
  render(){
  return (

    <div>
      <div><Topbar/></div>

      <div>
        <Container>  
        <p>Equation : {equation}</p>
        <p>X<sub>L</sub> : {xl}</p>
        <p>X<sub>R</sub> : {xr}</p>


        <form onSubmit={handleSubmit}>
            <label>
              Equation :
              <input type="text" value={equation} onChange={this.setEquation}
              />
            </label>
            <br></br>
            <label>
              X<sub>L</sub> :
              <input type="text" value={xl} onChange={this.setXL}
              />
            </label>
            <br></br>
            <label>
              X<sub>R</sub> :
              <input type="text" value={xr} onChange={this.setXR}/>
            </label>
            <br></br> 
            {btnState === 0  ? <button type="submit" disabled={btnState} value="Submit">Submit</button> : <button type="submit" disabled={!btnState} value="Submit">Reset</button> }

        </form>
        <div>
      </div>
        <br></br><br></br>
        </Container>
          <Container text> 
          <div>
           <table id="output">
              <tr>
                <th width="20%">Iteration</th>
                <th width="25%">X<sub>L</sub></th>
                <th width="25%">X<sub>R</sub></th>
                <th width="30%">X<sub>M</sub></th>
                <th width="30%">Error</th>
              </tr>
              <tr class="list-data">
                <td width="20%" id="Iteration"></td>
                <td width="25%" id="xl1"></td>
                <td width="25%" id="xr1"></td>
                <td width="30%" id="x"></td>
                <td width="30%" id="error"></td>
              </tr>
            </table>
          </div>
          </Container> 
      </div>

    </div>
  );}
}
