import React, { useState } from 'react';
import * as math from 'mathjs';
import { Container } from 'react-bootstrap';
import { Table } from 'antd';

import '../App.css';

import Topbar from './Topbar';



const axios = require('axios');

const header = [{
  title: 'Iteration',
  dataIndex: 'iteration',
  width: 50,
}, {
  title: 'Xl',
  dataIndex: 'xl',
  width: 300,
}, {
  title: 'Xr',
  dataIndex: 'xr',
  width: 300,
}, {
  title: 'Xm',
  dataIndex: 'xm',
  width: 300,
}, {
  title: 'Error',
  dataIndex: 'Error',
  width: 300,
}]

var tmpArr = [];


export default function Bisection(){
  const [equation, setEquation] = useState("");
  const [btnState, setBtnState] = useState(0);
  let [xl, setXL] = useState(0);
  let [xr, setXR] = useState(0);
  const[result,setResultArr] = useState(tmpArr);


  const handleSubmit = (e) => {
    if(btnState === 0)
    {
      e.preventDefault();
      bisection();
      setBtnState(1);
    }
  };

  const bisection = () => {

    axios
    .post("http://localhost:3333/api/BisectionAPI", {
      xl: parseFloat(xl),
      xr: parseFloat(xr),
      equation: equation,
    })
    .then(res => {
      setResultArr([...result,res.data.tmpArr]);
    })
    .catch(err => {
      console.log(err);
    });
  }


  return (
    <div>
      <div><Topbar/></div>
      <p></p>
            <div>
            <Container>  
              <h1>Bisection Method</h1>
              <p>Equation :<span>&nbsp;&nbsp;</span>{equation}</p>
              <p>X<sub>L</sub> :<span>&nbsp;&nbsp;</span>{xl}</p>
              <p>X<sub>R</sub> :<span>&nbsp;&nbsp;</span>{xr}</p>


              <form onSubmit={handleSubmit}>
                  <label>
                    Equation :<span>&nbsp;&nbsp;</span>
                    <input type="text" value={equation} onChange={e => setEquation(e.target.value)}
                    />
                  </label>
                  <p></p>
                  <label>
                    X<sub>L</sub> :<span>&nbsp;&nbsp;</span>
                    <input type="text" value={xl} onChange={e => setXL(e.target.value)}
                    />
                  </label>
                  <p></p>
                  <label>
                    X<sub>R</sub> :<span>&nbsp;&nbsp;</span>
                    <input type="text" value={xr} onChange={e => setXR(e.target.value)}/>
                  </label>
                  <p></p>

                  {btnState === 0  ? 
                  <button type="submit" disabled={btnState} value="Submit">Submit</button> :
                  <button type="submit" disabled={!btnState} value="Submit">Reset</button> }
                </form>
                <p></p><p></p>


            <div>

              {<Table dataSource={result}  columns={header} pagination={false} />}
            </div>
          </Container>

      </div>

    </div>
  );
}
